import axios from 'axios'
import React from 'react'
import { useState } from 'react'
import { useContext } from 'react'
import { useNavigate } from 'react-router-dom'
import { AuthContext } from '../../Context/AuthContext'

import "./Login.css"
const getdata = ({email,password})=>{
    return axios.post("https://reqres.in/api/login",{
        email:email,
        password:password
    })
}
const Login = () => {
    const {Auth,handleAuth,setToken}= useContext(AuthContext)
    const navigate = useNavigate()
    const [cred,setcred] = useState({
        email:"",
        password:""
    })
    const handleChange= (e)=>{
        const {name,value}  = e.target

        setcred({...cred,[name]:value})
    }
    const handleSubmit = (e)=>{
        e.preventDefault()
        console.log(cred)
        getdata(cred).then((res)=>{
            console.log(res.data)
            setToken({...res.data})
            handleAuth()
            navigate("/login/dashboard")
        })
        .catch((er)=>console.log(er))
        .finally(()=>console.log("!finally completed"))
    }

    
    console.log(Auth,handleAuth)

  return (
            <>
    <div className='Auth-page'>
      <div className='Auth-page-img'>
        <img src="https://auth.ayoa.com/images/ayoa-icon.png" alt="" />
      </div>
    </div>
     <div className='SignIn'>
        <p>Sign in to Ayoa</p>
        <form onSubmit={handleSubmit}>
        <div>
        <input type="email" placeholder='Email' name="email" onChange={handleChange} />
        </div>
        <br />
        <div>
        <input type="password" placeholder='Password' name="password" onChange={handleChange} />
        </div>
        <br />
        <div>
        <input type="Submit" value="SIGN IN WITH EMAIL" />
        </div>
        <br />
        </form>
        <p>or Sign in with</p>
    </div>
    <hr style={{width:"30%",margin:"auto"}}/>
    <div className='icons'>
        <div>
            <img style={{width:"15%"}} src="data:image/png;base64," alt="google" />
            <span>Google</span>
        </div>
        <div>
            <img style={{width:"15%"}} src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fb/Facebook_icon_2013.svg/2048px-Facebook_icon_2013.svg.png" alt="google" />
            <span>FaceBook</span>
        </div>
        <div>
            <img style={{width:"15%"}} src="https://upload.wikimedia.org/wikipedia/commons/thumb/f/fa/Apple_logo_black.svg/1200px-Apple_logo_black.svg.png" alt="google" />
            <span>Apple</span>
        </div>
    </div>
    <hr style={{width:"30%",margin:"auto"}}/>
    <div className='final-para'>
        <div style={{fontWeight:"700"}}>
           Dont have account ? <span style={{color:"blue",marginLeft:"10px"}}>Sign up</span>
        </div>
        <div>
            forgot password
        </div>
        <div>
        This site is protected by reCAPTCHA and the Google <span style={{color:"blue"}}>Privacy Policy</span> <br /> <span style={{color:"blue"}}>Terms of Service </span> apply.
        </div>
    </div>
    <hr style={{width:"30%",margin:"auto"}}/>
    <div className='open-image'>
        <img src="https://auth.ayoa.com/images/og_logo.svg" alt="" />
    </div>
            </>
  )
}

export default Login
